function CopyButton({ text }: { text: string }) {
  const [copied, setCopied] = useState(false);
  return (
    <button
      onClick={() => {
        navigator.clipboard.writeText(text);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      }}
      className={`px-3 py-1 text-xs rounded-md font-medium transition-all cursor-pointer ${
        copied
          ? "bg-emerald-100 text-emerald-700"
          : "bg-[var(--card-bg)] border border-[var(--border)] text-[var(--muted)] hover:text-[var(--accent)] hover:border-[var(--accent)]"
      }`}
    >
      {copied ? "Copied!" : "Copy"}
    </button>
  );
}

function FAQItem({ q, a }: { q: string; a: string }) {
  const [open, setOpen] = useState(false);
  return (
    <div className="border-b border-[var(--border)]">
      <button
        onClick={() => setOpen(!open)}
        className="w-full flex justify-between items-center py-4 text-left font-semibold text-[var(--foreground)] hover:text-[var(--accent)] transition-colors cursor-pointer"
      >
        <span>{q}</span>
        <span className="text-[var(--accent)] text-xl ml-4 shrink-0">{open ? "−" : "+"}</span>
      </button>
      {open && <p className="pb-4 text-[var(--muted)] leading-relaxed">{a}</p>}
    </div>
  );
}

