/* ── Page Component ──────────────────────────────────────────── */

export default function AIPromptLibraryPage() {
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [selectedTool, setSelectedTool] = useState<string | null>(null);
  const [visible, setVisible] = useState(48);
  const [expandedIds, setExpandedIds] = useState<Set<number>>(new Set());

  const filtered = useMemo(() => {
    const q = search.toLowerCase();
    return PROMPTS.filter((p) => {
      const matchesSearch =
        !q || p.title.toLowerCase().includes(q) || p.prompt.toLowerCase().includes(q);
      const matchesCategory = !selectedCategory || p.category === selectedCategory;
      const matchesTool =
        !selectedTool ||
        p.tools.includes(selectedTool) ||
        p.tools.includes("Any");
      return matchesSearch && matchesCategory && matchesTool;
    });
  }, [search, selectedCategory, selectedTool]);

  const visiblePrompts = filtered.slice(0, visible);
  const hasMore = visible < filtered.length;

  function toggleExpand(id: number) {
    setExpandedIds((prev) => {
      const next = new Set(prev);
      if (next.has(id)) next.delete(id);
      else next.add(id);
      return next;
    });
  }

  return (
    <div className="max-w-5xl mx-auto px-4 py-8 sm:py-12">

      {/* ── Hero ─────────────────────────────────────────────── */}
      <header className="text-center mb-12">
        <p className="text-sm font-medium text-[var(--accent)] mb-2 tracking-wide uppercase">
          Free Resource — Updated February 2026
        </p>
        <h1 className="text-3xl sm:text-4xl lg:text-5xl font-extrabold mb-4 leading-tight">
          Free{" "}
          <span className="bg-gradient-to-r from-violet-600 to-indigo-600 bg-clip-text text-transparent">
            AI Prompt Library
          </span>
        </h1>
        <p className="text-lg text-[var(--muted)] max-w-2xl mx-auto mb-6">
          500+ battle-tested prompts for ChatGPT, Claude, Gemini, Midjourney, and more.
          Copy, customize the bracketed placeholders, and get better AI outputs instantly.
        </p>
        <div className="flex flex-wrap justify-center gap-3 text-sm">
          {[
            "500+ prompts",
            "8 categories",
            "Free forever",
            "Copy with one click",
          ].map((pill) => (
            <span
              key={pill}
              className="px-3 py-1.5 bg-[var(--card-bg)] border border-[var(--border)] rounded-full"
            >
              {pill}
            </span>
          ))}
        </div>
      </header>

      {/* ── Search ───────────────────────────────────────────── */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search prompts by title or keyword..."
          value={search}
          onChange={(e) => {
            setSearch(e.target.value);
            setVisible(48);
          }}
          className="w-full px-4 py-3 rounded-xl border border-[var(--border)] bg-white text-[var(--foreground)] placeholder-[var(--muted)] focus:outline-none focus:ring-2 focus:ring-[var(--accent)] focus:border-transparent text-base"
        />
      </div>

      {/* ── Category Filters ─────────────────────────────────── */}
      <div className="mb-4 flex flex-wrap gap-2">
        <button
          onClick={() => { setSelectedCategory(null); setVisible(48); }}
          className={`px-3 py-1.5 rounded-full text-sm font-medium transition-colors cursor-pointer ${
            selectedCategory === null
              ? "bg-[var(--accent)] text-white"
              : "bg-[var(--card-bg)] border border-[var(--border)] text-[var(--muted)] hover:text-[var(--accent)]"
          }`}
        >
          All Categories
        </button>
        {CATEGORIES.map((cat) => (
          <button
            key={cat}
            onClick={() => { setSelectedCategory(selectedCategory === cat ? null : cat); setVisible(48); }}
            className={`px-3 py-1.5 rounded-full text-sm font-medium transition-colors cursor-pointer ${
              selectedCategory === cat
                ? "bg-[var(--accent)] text-white"
                : "bg-[var(--card-bg)] border border-[var(--border)] text-[var(--muted)] hover:text-[var(--accent)]"
            }`}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* ── Tool Filters ─────────────────────────────────────── */}
      <div className="mb-8 flex flex-wrap gap-2">
        <button
          onClick={() => { setSelectedTool(null); setVisible(48); }}
          className={`px-3 py-1 rounded-full text-xs font-medium transition-colors cursor-pointer ${
            selectedTool === null
              ? "bg-gray-800 text-white"
              : "bg-[var(--card-bg)] border border-[var(--border)] text-[var(--muted)] hover:text-[var(--foreground)]"
          }`}
        >
          All Tools
        </button>
        {TOOLS.map((tool) => (
          <button
            key={tool}
            onClick={() => { setSelectedTool(selectedTool === tool ? null : tool); setVisible(48); }}
            className={`px-3 py-1 rounded-full text-xs font-medium transition-colors cursor-pointer ${
              selectedTool === tool
                ? "bg-gray-800 text-white"
                : "bg-[var(--card-bg)] border border-[var(--border)] text-[var(--muted)] hover:text-[var(--foreground)]"
            }`}
          >
            {tool}
          </button>
        ))}
      </div>

      {/* ── Results Counter ──────────────────────────────────── */}
      <p className="text-sm text-[var(--muted)] mb-6">
        Showing{" "}
        <span className="font-semibold text-[var(--foreground)]">
          {Math.min(visible, filtered.length)}
        </span>{" "}
        of{" "}
        <span className="font-semibold text-[var(--foreground)]">
          {filtered.length}
        </span>{" "}
        prompts
        {selectedCategory && (
          <> in <span className="font-semibold text-[var(--accent)]">{selectedCategory}</span></>
        )}
        {search && (
          <> matching <span className="font-semibold text-[var(--accent)]">&ldquo;{search}&rdquo;</span></>
        )}
      </p>

      {/* ── Prompt Grid ──────────────────────────────────────── */}
      {filtered.length === 0 ? (
        <div className="text-center py-16 text-[var(--muted)]">
          <p className="text-4xl mb-4">🔍</p>
          <p className="text-lg font-medium mb-2">No prompts found</p>
          <p className="text-sm">Try different keywords or remove a filter.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-8">
          {visiblePrompts.map((prompt, i) => {
            const isExpanded = expandedIds.has(prompt.id);
            const isLong = prompt.prompt.length > 300;
            const displayText =
              isExpanded || !isLong
                ? prompt.prompt
                : prompt.prompt.slice(0, 300) + "…";

            return (
              <div key={prompt.id}>
                {/* Ad slot after first 24 cards */}
                {i === 24 && (
                  <div className="col-span-full my-6 min-h-[90px]">
                    <div id="tinyadz-inline-1" className="w-full" />
                  </div>
                )}
                <div className="bg-white border border-[var(--border)] rounded-xl p-5 shadow-sm flex flex-col gap-3 hover:border-[var(--accent)]/40 transition-colors">
                  {/* Header */}
                  <div className="flex items-start justify-between gap-2">
                    <div className="flex-1 min-w-0">
                      <span
                        className={`inline-block px-2 py-0.5 rounded text-xs font-semibold mb-1.5 ${categoryColors[prompt.category] ?? "bg-gray-100 text-gray-700"}`}
                      >
                        {prompt.category}
                      </span>
                      <h3 className="font-bold text-[var(--foreground)] leading-snug">
                        {prompt.title}
                      </h3>
                    </div>
                    <CopyButton text={prompt.prompt} />
                  </div>

                  {/* Prompt text */}
                  <p className="text-sm text-[var(--muted)] leading-relaxed font-mono whitespace-pre-wrap break-words">
                    {displayText}
                  </p>

                  {/* Expand / collapse */}
                  {isLong && (
                    <button
                      onClick={() => toggleExpand(prompt.id)}
                      className="text-xs text-[var(--accent)] hover:text-[var(--accent-light)] font-medium text-left cursor-pointer"
                    >
                      {isExpanded ? "Show less ↑" : "Show full prompt ↓"}
                    </button>
                  )}

                  {/* Tool badges */}
                  <div className="flex flex-wrap gap-1 pt-1 border-t border-[var(--border)]">
                    {prompt.tools.map((tool) => (
                      <span
                        key={tool}
                        className="px-2 py-0.5 rounded-full bg-gray-100 text-gray-600 text-xs font-medium"
                      >
                        {tool}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* ── Load More ────────────────────────────────────────── */}
      {hasMore && (
        <div className="text-center mb-12">
          <button
            onClick={() => setVisible((v) => v + 48)}
            className="px-6 py-3 bg-[var(--accent)] text-white rounded-xl font-semibold hover:bg-[var(--accent-light)] transition-colors cursor-pointer"
          >
            Load more prompts ({filtered.length - visible} remaining)
          </button>
        </div>
      )}

      {/* ── FAQ ──────────────────────────────────────────────── */}
      <section id="faq" className="mb-12">
        <h2 className="text-2xl sm:text-3xl font-bold mb-6">
          Frequently Asked Questions
        </h2>
        <div className="bg-white border border-[var(--border)] rounded-xl p-6">
          {faqs.map((f) => (
            <FAQItem key={f.q} q={f.q} a={f.a} />
          ))}
        </div>
      </section>

      {/* ── Ad Slot 2 ────────────────────────────────────────── */}
      <div className="my-10 min-h-[90px]">
        <div id="tinyadz-inline-2" className="w-full" />
      </div>

      {/* ── Internal Links ───────────────────────────────────── */}
      <section className="mb-12">
        <div className="bg-[var(--card-bg)] border border-[var(--border)] rounded-xl p-6">
          <h3 className="font-bold mb-4 text-lg">More from ArloBuilds</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
            <a
              href="/mcp-servers"
              className="flex items-start gap-2 text-sm text-[var(--accent)] hover:text-[var(--accent-light)] no-underline p-3 rounded-lg hover:bg-[var(--accent-bg)] transition-colors"
            >
              <span className="mt-0.5">→</span>
              <span>Best MCP Servers 2026: 25 Tested &amp; Ranked</span>
            </a>
            <a
              href="/cursor-alternatives"
              className="flex items-start gap-2 text-sm text-[var(--accent)] hover:text-[var(--accent-light)] no-underline p-3 rounded-lg hover:bg-[var(--accent-bg)] transition-colors"
            >
              <span className="mt-0.5">→</span>
              <span>Best Cursor Alternatives 2026: 10 Compared</span>
            </a>
            <a
              href="/seedance-pricing"
              className="flex items-start gap-2 text-sm text-[var(--accent)] hover:text-[var(--accent-light)] no-underline p-3 rounded-lg hover:bg-[var(--accent-bg)] transition-colors"
            >
              <span className="mt-0.5">→</span>
              <span>Seedance 2.0 Pricing Guide</span>
            </a>
            <a
              href="/mcp-servers-claude-code"
              className="flex items-start gap-2 text-sm text-[var(--accent)] hover:text-[var(--accent-light)] no-underline p-3 rounded-lg hover:bg-[var(--accent-bg)] transition-colors"
            >
              <span className="mt-0.5">→</span>
              <span>Best MCP Servers for Claude Code</span>
            </a>
          </div>
        </div>
      </section>

      {/* Agent Playbook CTA */}
      <section className="mb-12 bg-gray-900 border border-gray-700 rounded-xl p-6 sm:p-8 text-center">
        <p className="text-xs font-semibold text-emerald-400 tracking-wider uppercase mb-2">From ArloBuilds</p>
        <p className="text-lg font-bold text-white mb-2">The Agent Playbook</p>
        <p className="text-sm text-gray-400 mb-4 max-w-lg mx-auto">
          Turn these prompts into an autonomous system. Learn how to orchestrate 7 AI agents with SOUL files, queue messaging, and shared memory.
        </p>
        <a href="/agent-playbook" className="inline-block px-6 py-2.5 rounded-lg bg-emerald-500 text-gray-950 font-semibold text-sm hover:bg-emerald-400 transition-colors">
          Learn more — $29
        </a>
      </section>

      {/* ── Footer ───────────────────────────────────────────── */}
      <footer className="border-t border-[var(--border)] pt-8 pb-12 text-center text-sm text-[var(--muted)]">
        <p className="mb-2">
          Last updated: February 2026. Free to use for personal and commercial projects.
        </p>
        <p>
          ArloBuilds is independent. Share these prompts freely — just link back.
        </p>
      </footer>
    </div>
  );
}
